@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 60 7.6923% 97.4510%;
    --foreground: 220.0000 14.7541% 11.9608%;
    --card: 0 0% 100%;
    --card-foreground: 0 0% 20%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 0% 20%;
    --primary: 177.1200 100.0000% 24.5098%;
    --primary-foreground: 0 0% 100%;
    --secondary: 218.0690 60.6695% 53.1373%;
    --secondary-foreground: 0 0% 100%;
    --muted: 180 13.0435% 81.9608%;
    --muted-foreground: 0 0% 20%;
    --accent: 180 13.0435% 81.9608%;
    --accent-foreground: 0 0% 0%;
    --destructive: 0 47.8261% 45.0980%;
    --destructive-foreground: 0 0% 100%;
    --border: 0 0% 87.8431%;
    --input: 60 0.4255% 46.0784%;
    --ring: 177.1200 100.0000% 24.5098%;
    --chart-1: 177.0968 100.0000% 24.3137%;
    --chart-2: 22.0000 35.2941% 33.3333%;
    --chart-3: 217.1429 21.6495% 19.0196%;
    --chart-4: 149.5385 37.1429% 34.3137%;
    --chart-5: 26.9159 100.0000% 20.9804%;
    --sidebar: 0 0% 100%;
    --sidebar-foreground: 0 0% 0%;
    --sidebar-primary: 177.1200 100.0000% 24.5098%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 175 24.3243% 29.0196%;
    --sidebar-accent-foreground: 0 0% 100%;
    --sidebar-border: 60 0.4255% 46.0784%;
    --sidebar-ring: 177.1200 100.0000% 24.5098%;
    --font-sans: Roboto Mono, monospace;
    --font-serif: Roboto Mono, monospace;
    --font-mono: Roboto Mono, monospace;
    --radius: 0.5rem;
    --shadow-2xs: 0px 5px 10px -2px hsl(0 0% 0% / 0.05);
    --shadow-xs: 0px 5px 10px -2px hsl(0 0% 0% / 0.05);
    --shadow-sm: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 1px 2px -3px hsl(0 0% 0% / 0.10);
    --shadow: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 1px 2px -3px hsl(0 0% 0% / 0.10);
    --shadow-md: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 2px 4px -3px hsl(0 0% 0% / 0.10);
    --shadow-lg: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 4px 6px -3px hsl(0 0% 0% / 0.10);
    --shadow-xl: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 8px 10px -3px hsl(0 0% 0% / 0.10);
    --shadow-2xl: 0px 5px 10px -2px hsl(0 0% 0% / 0.25);
    --tracking-normal: 0em;
    --spacing: 0.25rem;
  }

  .dark {
    --background: 220.0000 14.7541% 11.9608%;
    --foreground: 0 0% 100%;
    --card: 192.0000 5.7471% 17.0588%;
    --card-foreground: 0 0% 100%;
    --popover: 197.1429 6.9307% 19.8039%;
    --popover-foreground: 0 0% 100%;
    --primary: 176.1600 64.1026% 38.2353%;
    --primary-foreground: 0 0% 100%;
    --secondary: 218.1457 75.8794% 60.9804%;
    --secondary-foreground: 0 0% 100%;
    --muted: 0 0% 26.6667%;
    --muted-foreground: 0 0% 63.9216%;
    --accent: 177.1429 37.5000% 21.9608%;
    --accent-foreground: 0 0% 89.8039%;
    --destructive: 0 84.2365% 60.1961%;
    --destructive-foreground: 0 0% 100%;
    --border: 0 0% 26.6667%;
    --input: 0 0% 26.6667%;
    --ring: 176.1600 64.1026% 38.2353%;
    --chart-1: 176.1600 64.1026% 38.2353%;
    --chart-2: 23.2500 57.1429% 72.5490%;
    --chart-3: 221.2500 26.6667% 88.2353%;
    --chart-4: 141.8182 21.2903% 69.6078%;
    --chart-5: 36.8627 100.0000% 70%;
    --sidebar: 220.0000 14.7541% 11.9608%;
    --sidebar-foreground: 0 0% 89.8039%;
    --sidebar-primary: 176.1600 64.1026% 38.2353%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 177.1429 37.5000% 21.9608%;
    --sidebar-accent-foreground: 0 0% 89.8039%;
    --sidebar-border: 0 0% 26.6667%;
    --sidebar-ring: 176.1600 64.1026% 38.2353%;
    --font-sans: Roboto Mono, monospace;
    --font-serif: Roboto Mono, monospace;
    --font-mono: Roboto Mono, monospace;
    --radius: 0.5rem;
    --shadow-2xs: 0px 5px 10px -2px hsl(0 0% 0% / 0.05);
    --shadow-xs: 0px 5px 10px -2px hsl(0 0% 0% / 0.05);
    --shadow-sm: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 1px 2px -3px hsl(0 0% 0% / 0.10);
    --shadow: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 1px 2px -3px hsl(0 0% 0% / 0.10);
    --shadow-md: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 2px 4px -3px hsl(0 0% 0% / 0.10);
    --shadow-lg: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 4px 6px -3px hsl(0 0% 0% / 0.10);
    --shadow-xl: 0px 5px 10px -2px hsl(0 0% 0% / 0.10), 0px 8px 10px -3px hsl(0 0% 0% / 0.10);
    --shadow-2xl: 0px 5px 10px -2px hsl(0 0% 0% / 0.25);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

import type { Config } from "tailwindcss"
import tailwindAnimate from "tailwindcss-animate"

export default {
  darkMode: ["class"],
  content: [
    "./pages/**/*.{js,ts,jsx,tsx,mdx}",
    "./components/**/*.{js,ts,jsx,tsx,mdx}",
    "./app/**/*.{js,ts,jsx,tsx,mdx}",
    "./registry/**/*.{js,ts,jsx,tsx,mdx}",
  ],
  theme: {
    extend: {
      colors: {
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        card: {
          DEFAULT: "hsl(var(--card))",
          foreground: "hsl(var(--card-foreground))",
        },
        popover: {
          DEFAULT: "hsl(var(--popover))",
          foreground: "hsl(var(--popover-foreground))",
        },
        primary: {
          DEFAULT: "hsl(var(--primary))",
          foreground: "hsl(var(--primary-foreground))",
        },
        secondary: {
          DEFAULT: "hsl(var(--secondary))",
          foreground: "hsl(var(--secondary-foreground))",
        },
        muted: {
          DEFAULT: "hsl(var(--muted))",
          foreground: "hsl(var(--muted-foreground))",
        },
        accent: {
          DEFAULT: "hsl(var(--accent))",
          foreground: "hsl(var(--accent-foreground))",
        },
        destructive: {
          DEFAULT: "hsl(var(--destructive))",
          foreground: "hsl(var(--destructive-foreground))",
        },
        border: "hsl(var(--border))",
        input: "hsl(var(--input))",
        ring: "hsl(var(--ring))",
        chart: {
          "1": "hsl(var(--chart-1))",
          "2": "hsl(var(--chart-2))",
          "3": "hsl(var(--chart-3))",
          "4": "hsl(var(--chart-4))",
          "5": "hsl(var(--chart-5))",
        },
      },
      borderRadius: {
        lg: "var(--radius)",
        md: "calc(var(--radius) - 2px)",
        sm: "calc(var(--radius) - 4px)",
      },
    },
  },
  plugins: [tailwindAnimate],
} satisfies Config
